"use client";

import { Card, CardContent, Typography, Box } from '@mui/material';
import { useEffect, useState } from 'react';
import Carousel from "react-material-ui-carousel";

export default function ButtonInfoSlider() {
    const [isClient, setIsClient] = useState(false);

    useEffect(() => {
        setIsClient(true); // This will ensure the component renders only on the client
    }, []);

    if (!isClient) {
        return null; // Render nothing on the server
    }

    return (
        <div className="">
            <h1 className="header_h1 absolute opacity-0">Button Information Slider</h1>
            <Box sx={{ maxWidth: 900, margin: '0 auto' }}>
                <Carousel
                    autoPlay={false}
                    interval={3000}
                    animation="fade"
                    navButtonsAlwaysVisible={true}
                    swipe={true}
                    indicators={false}
                >
                    {getItems().map((item, index) => (
                        <Card key={index} sx={{ minHeight: 200 }}>
                            <CardContent className={"flex flex-row"}>
                                <Base_card
                                    title={item.title}
                                    description={item.description}
                                    icon={item.icon}
                                    quote={item.quote}
                                    annotation={item.annotation}
                                />
                            </CardContent>
                        </Card>
                    ))}
                </Carousel>
            </Box>
        </div>
    );
}

export function Base_card({ title, description, icon, quote, annotation }: { title: string, description: string, icon: string, quote: string, annotation: string }) {
    return (
        <Box
            sx={{ display: 'flex', alignItems: 'center', gap: 2 }}
            className="relative p-6 border border-gray-200 rounded-lg bg-white shadow-lg"
        >
            {/* Icon */}
            <Typography variant="h3" component="div" className="header_h3 text-6xl text-indigo-400">
                {icon}
            </Typography>

            {/* Card Content */}
            <CardContent className="space-y-4">
                {/* Title */}
                <Typography variant="h5" component="div" className="indent-4 header_h3">
                    {title}
                </Typography>

                {/* Description */}
                <Typography variant="body1" color="text.secondary" className="paragraph indent-4">
                    {description}
                </Typography>

                {/* Quote */}
                <Typography variant="body2" color="text.secondary" className="paragraph indent-4">
                    <blockquote
                        className="font-semibold italic mb-4 relative pl-8 before:content-['‚Äú'] before:absolute before:left-0 before:top-0 before:text-5xl before:font-bold before:text-indigo-300 after:content-['‚Äù'] after:text-5xl after:font-bold after:text-indigo-300">
                        {quote}
                    </blockquote>
                </Typography>

                {/* Annotation */}
                <div className="text-gray-700 text-base bg-indigo-50 p-4 rounded-lg shadow-inner">
                    <p className="italic">
                        <span className="font-bold text-purple-600">–ê–Ω–Ω–æ—Ç–∞—Ü–∏—è:</span> {annotation}
                    </p>
                </div>
            </CardContent>
        </Box>
    );
}

